Smart::StatusCode EnforcerTemplate::connectEnforcementInstruction_Send(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	if(connections.enforcementInstruction_Send.initialConnect == false) {
		return Smart::SMART_OK;
	}
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = enforcementInstruction_Send->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->enforcementInstruction_Send->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	return status;
}
