/**
 * First connect ALL client ports contained in this component, then start all services:
 * activate state, push, etc...
 */
Smart::StatusCode EnforcerTemplate::connectAndStartAllServices() {
	Smart::StatusCode status = Smart::SMART_OK;
	
	status = connectEnforcementInstruction_Send(connections.enforcementInstruction_Send.serverName, connections.enforcementInstruction_Send.serviceName);
	if(status != Smart::SMART_OK) return status;
	status = connectEnforcementReply_Push(connections.enforcementReply_Push.serverName, connections.enforcementReply_Push.serviceName);
	if(status != Smart::SMART_OK) return status;
	status = connectEnforcementReport_Push(connections.enforcementReport_Push.serverName, connections.enforcementReport_Push.serviceName);
	if(status != Smart::SMART_OK) return status;
	status = connectEnforcement_QueryRequester(connections.enforcement_QueryRequester.serverName, connections.enforcement_QueryRequester.serviceName);
	if(status != Smart::SMART_OK) return status;
	status = connectEvent_Listener(connections.event_Listener.serverName, connections.event_Listener.serviceName);
	if(status != Smart::SMART_OK) return status;
	status = connectReport_QueryRequester(connections.report_QueryRequester.serverName, connections.report_QueryRequester.serviceName);
	if(status != Smart::SMART_OK) return status;
	return status;
}

/**
 * Start all tasks contained in this component.
 */
void EnforcerTemplate::startAllTasks() {
	// start task EnforcementManager
	if(connections.enforcementManager.scheduler != "DEFAULT") {
		ACE_Sched_Params enforcementManager_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.enforcementManager.scheduler == "FIFO") {
			enforcementManager_SchedParams.policy(ACE_SCHED_FIFO);
			enforcementManager_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.enforcementManager.scheduler == "RR") {
			enforcementManager_SchedParams.policy(ACE_SCHED_RR);
			enforcementManager_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		enforcementManager->start(enforcementManager_SchedParams, connections.enforcementManager.cpuAffinity);
	} else {
		enforcementManager->start();
	}
	// start task EventManager
	if(connections.eventManager.scheduler != "DEFAULT") {
		ACE_Sched_Params eventManager_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.eventManager.scheduler == "FIFO") {
			eventManager_SchedParams.policy(ACE_SCHED_FIFO);
			eventManager_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.eventManager.scheduler == "RR") {
			eventManager_SchedParams.policy(ACE_SCHED_RR);
			eventManager_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		eventManager->start(eventManager_SchedParams, connections.eventManager.cpuAffinity);
	} else {
		eventManager->start();
	}
}

