Smart::TaskTriggerSubject* EnforcerTemplate::getInputTaskTriggerFromString(const std::string &client)
{
	if(client == "EnforcementReply_Push") return enforcementReply_PushInputTaskTrigger;
	if(client == "EnforcementReply_Send") return enforcementReply_SendInputTaskTrigger;
	if(client == "EnforcementReport_Push") return enforcementReport_PushInputTaskTrigger;
	if(client == "EnforcementReport_Send") return enforcementReport_SendInputTaskTrigger;
	if(client == "Event_Listener") return event_ListenerInputTaskTrigger;
	
	return NULL;
}


void EnforcerTemplate::init(int argc, char *argv[])
{
	try {
		Smart::StatusCode status;
		
		// load initial parameters from ini-file (if found)
		loadParameter(argc, argv);
		
		
		
		// initialize all registered port-factories
		for(auto portFactory = portFactoryRegistry.begin(); portFactory != portFactoryRegistry.end(); portFactory++) 
		{
			portFactory->second->initialize(this, argc, argv);
		}
		
		// initialize all registered component-extensions
		for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
		{
			extension->second->initialize(this, argc, argv);
		}
		
		EnforcerTemplatePortFactoryInterface *acePortFactory = portFactoryRegistry["ACE_SmartSoft"];
		if(acePortFactory == 0) {
			std::cerr << "ERROR: acePortFactory NOT instantiated -> exit(-1)" << std::endl;
			exit(-1);
		}
		
		// this pointer is used for backwards compatibility (deprecated: should be removed as soon as all patterns, including coordination, are moved to port-factory)
		SmartACE::SmartComponent *component = dynamic_cast<EnforcerTemplateAcePortFactory*>(acePortFactory)->getComponentImpl();
		
		std::cout << "ComponentDefinition EnforcerTemplate is named " << connections.component.name << std::endl;
		
		if(connections.component.useLogger == true) {
			//FIXME: use logging
			//Smart::LOGGER->openLogFileInFolder("data/"+connections.component.name);
			//Smart::LOGGER->startLogging();
		}

		// create event-test handlers (if needed)
		event_CreatorEventTestHandler = std::make_shared<Event_CreatorEventTestHandler>();
		
		// create server ports
		// TODO: set minCycleTime from Ini-file
		enforcementInstruction_Push = portFactoryRegistry[connections.enforcementInstruction_Push.roboticMiddleware]->createEnforcementInstruction_Push(connections.enforcementInstruction_Push.serviceName);
		enforcementInstruction_PushWrapper = new EnforcementInstruction_PushWrapper(enforcementInstruction_Push);
		enforcementReply_Send = portFactoryRegistry[connections.enforcementReply_Send.roboticMiddleware]->createEnforcementReply_Send(connections.enforcementReply_Send.serviceName);
		enforcementReport_Send = portFactoryRegistry[connections.enforcementReport_Send.roboticMiddleware]->createEnforcementReport_Send(connections.enforcementReport_Send.serviceName);
		event_CreatorEventTestHandler = std::make_shared<Event_CreatorEventTestHandler>();
		event_Creator = portFactoryRegistry[connections.event_Creator.roboticMiddleware]->createEvent_Creator(connections.event_Creator.serviceName, event_CreatorEventTestHandler);
		event_CreatorWrapper = new Event_CreatorWrapper(event_Creator);
		
		// create client ports
		enforcementInstruction_Send = portFactoryRegistry[connections.enforcementInstruction_Send.roboticMiddleware]->createEnforcementInstruction_Send();
		enforcementInstruction_SendWrapper = new EnforcementInstruction_SendWrapper(enforcementInstruction_Send);
		enforcementReply_Push = portFactoryRegistry[connections.enforcementReply_Push.roboticMiddleware]->createEnforcementReply_Push();
		enforcementReport_Push = portFactoryRegistry[connections.enforcementReport_Push.roboticMiddleware]->createEnforcementReport_Push();
		enforcement_QueryRequester = portFactoryRegistry[connections.enforcement_QueryRequester.roboticMiddleware]->createEnforcement_QueryRequester();
		event_Listener = portFactoryRegistry[connections.event_Listener.roboticMiddleware]->createEvent_Listener();
		report_QueryRequester = portFactoryRegistry[connections.report_QueryRequester.roboticMiddleware]->createReport_QueryRequester();
		
		// create InputTaskTriggers and UpcallManagers
		enforcementReply_PushInputCollector = new EnforcementReply_PushInputCollector(enforcementReply_Push);
		enforcementReply_PushInputTaskTrigger = new Smart::InputTaskTrigger<SmartInstitutionsServiceRepository::EnforcementReplyPackage>(enforcementReply_PushInputCollector);
		enforcementReply_PushUpcallManager = new EnforcementReply_PushUpcallManager(enforcementReply_PushInputCollector);
		enforcementReply_SendInputCollector = new EnforcementReply_SendInputCollector(enforcementReply_Send);
		enforcementReply_SendInputTaskTrigger = new Smart::InputTaskTrigger<SmartInstitutionsServiceRepository::EnforcementReplyPackage>(enforcementReply_SendInputCollector);
		enforcementReply_SendUpcallManager = new EnforcementReply_SendUpcallManager(enforcementReply_SendInputCollector);
		enforcementReport_PushInputCollector = new EnforcementReport_PushInputCollector(enforcementReport_Push);
		enforcementReport_PushInputTaskTrigger = new Smart::InputTaskTrigger<SmartInstitutionsServiceRepository::EnforcementReportPackage>(enforcementReport_PushInputCollector);
		enforcementReport_PushUpcallManager = new EnforcementReport_PushUpcallManager(enforcementReport_PushInputCollector);
		enforcementReport_SendInputCollector = new EnforcementReport_SendInputCollector(enforcementReport_Send);
		enforcementReport_SendInputTaskTrigger = new Smart::InputTaskTrigger<SmartInstitutionsServiceRepository::EnforcementReportPackage>(enforcementReport_SendInputCollector);
		enforcementReport_SendUpcallManager = new EnforcementReport_SendUpcallManager(enforcementReport_SendInputCollector);
		event_ListenerInputCollector = new Event_ListenerInputCollector(event_Listener);
		event_ListenerInputTaskTrigger = new Smart::InputTaskTrigger<Smart::EventInputType<SmartInstitutionsServiceRepository::SmartIN_EventType>>(event_ListenerInputCollector);
		event_ListenerUpcallManager = new Event_ListenerUpcallManager(event_ListenerInputCollector);
		
		// create input-handler
		
		// create request-handlers
		
		// create state pattern
		stateChangeHandler = new SmartStateChangeHandler();
		stateSlave = new SmartACE::StateSlave(component, stateChangeHandler);
		status = stateSlave->setUpInitialState(connections.component.initialComponentMode);
		if (status != Smart::SMART_OK) std::cerr << status << "; failed setting initial ComponentMode: " << connections.component.initialComponentMode << std::endl;
		// activate state slave
		status = stateSlave->activate();
		if(status != Smart::SMART_OK) std::cerr << "ERROR: activate state" << std::endl;
		
		wiringSlave = new SmartACE::WiringSlave(component);
		// add client port to wiring slave
		if(connections.enforcementInstruction_Send.roboticMiddleware == "ACE_SmartSoft") {
			//FIXME: this must also work with other implementations
			dynamic_cast<SmartACE::SendClient<SmartInstitutionsServiceRepository::EnforcementInstructionPackage>*>(enforcementInstruction_Send)->add(wiringSlave, connections.enforcementInstruction_Send.wiringName);
		}
		if(connections.enforcementReply_Push.roboticMiddleware == "ACE_SmartSoft") {
			//FIXME: this must also work with other implementations
			dynamic_cast<SmartACE::PushClient<SmartInstitutionsServiceRepository::EnforcementReplyPackage>*>(enforcementReply_Push)->add(wiringSlave, connections.enforcementReply_Push.wiringName);
		}
		if(connections.enforcementReport_Push.roboticMiddleware == "ACE_SmartSoft") {
			//FIXME: this must also work with other implementations
			dynamic_cast<SmartACE::PushClient<SmartInstitutionsServiceRepository::EnforcementReportPackage>*>(enforcementReport_Push)->add(wiringSlave, connections.enforcementReport_Push.wiringName);
		}
		if(connections.enforcement_QueryRequester.roboticMiddleware == "ACE_SmartSoft") {
			//FIXME: this must also work with other implementations
			dynamic_cast<SmartACE::QueryClient<SmartInstitutionsServiceRepository::MemberIdentifier, SmartInstitutionsServiceRepository::EnforcementReportPackage>*>(enforcement_QueryRequester)->add(wiringSlave, connections.enforcement_QueryRequester.wiringName);
		}
		if(connections.event_Listener.roboticMiddleware == "ACE_SmartSoft") {
			//FIXME: this must also work with other implementations
			dynamic_cast<SmartACE::EventClient<SmartInstitutionsServiceRepository::SmartIN_Command, SmartInstitutionsServiceRepository::SmartIN_EventType>*>(event_Listener)->add(wiringSlave, connections.event_Listener.wiringName);
		}
		if(connections.report_QueryRequester.roboticMiddleware == "ACE_SmartSoft") {
			//FIXME: this must also work with other implementations
			dynamic_cast<SmartACE::QueryClient<SmartInstitutionsServiceRepository::MemberIdentifier, SmartInstitutionsServiceRepository::EnforcementReportPackage>*>(report_QueryRequester)->add(wiringSlave, connections.report_QueryRequester.wiringName);
		}
		
		
		
		// create Task EnforcementManager
		enforcementManager = new EnforcementManager(component);
		// configure input-links
		enforcementReply_PushUpcallManager->attach(enforcementManager);
		enforcementReply_SendUpcallManager->attach(enforcementManager);
		enforcementReport_PushUpcallManager->attach(enforcementManager);
		enforcementReport_SendUpcallManager->attach(enforcementManager);
		// configure task-trigger (if task is configurable)
		if(connections.enforcementManager.trigger == "PeriodicTimer") {
			// create PeriodicTimerTrigger
			int microseconds = 1000*1000 / connections.enforcementManager.periodicActFreq;
			if(microseconds > 0) {
				Smart::TimedTaskTrigger *triggerPtr = new Smart::TimedTaskTrigger();
				triggerPtr->attach(enforcementManager);
				component->getTimerManager()->scheduleTimer(triggerPtr, (void *) 0, std::chrono::microseconds(microseconds), std::chrono::microseconds(microseconds));
				// store trigger in class member
				enforcementManagerTrigger = triggerPtr;
			} else {
				std::cerr << "ERROR: could not set-up Timer with cycle-time " << microseconds << " as activation source for Task EnforcementManager" << std::endl;
			}
		} else if(connections.enforcementManager.trigger == "DataTriggered") {
			enforcementManagerTrigger = getInputTaskTriggerFromString(connections.enforcementManager.inPortRef);
			if(enforcementManagerTrigger != NULL) {
				enforcementManagerTrigger->attach(enforcementManager, connections.enforcementManager.prescale);
			} else {
				std::cerr << "ERROR: could not set-up InPort " << connections.enforcementManager.inPortRef << " as activation source for Task EnforcementManager" << std::endl;
			}
		} 
		
		// create Task EventManager
		eventManager = new EventManager(component);
		// configure input-links
		event_ListenerUpcallManager->attach(eventManager);
		// configure task-trigger (if task is configurable)
		if(connections.eventManager.trigger == "PeriodicTimer") {
			// create PeriodicTimerTrigger
			int microseconds = 1000*1000 / connections.eventManager.periodicActFreq;
			if(microseconds > 0) {
				Smart::TimedTaskTrigger *triggerPtr = new Smart::TimedTaskTrigger();
				triggerPtr->attach(eventManager);
				component->getTimerManager()->scheduleTimer(triggerPtr, (void *) 0, std::chrono::microseconds(microseconds), std::chrono::microseconds(microseconds));
				// store trigger in class member
				eventManagerTrigger = triggerPtr;
			} else {
				std::cerr << "ERROR: could not set-up Timer with cycle-time " << microseconds << " as activation source for Task EventManager" << std::endl;
			}
		} else if(connections.eventManager.trigger == "DataTriggered") {
			eventManagerTrigger = getInputTaskTriggerFromString(connections.eventManager.inPortRef);
			if(eventManagerTrigger != NULL) {
				eventManagerTrigger->attach(eventManager, connections.eventManager.prescale);
			} else {
				std::cerr << "ERROR: could not set-up InPort " << connections.eventManager.inPortRef << " as activation source for Task EventManager" << std::endl;
			}
		} 
		
		
		// link observers with subjects
	} catch (const std::exception &ex) {
		std::cerr << "Uncaught std exception" << ex.what() << std::endl;
	} catch (...) {
		std::cerr << "Uncaught exception" << std::endl;
	}
}

// run the component
void EnforcerTemplate::run()
{
	stateSlave->acquire("init");
	// startup all registered port-factories
	for(auto portFactory = portFactoryRegistry.begin(); portFactory != portFactoryRegistry.end(); portFactory++) 
	{
		portFactory->second->onStartup();
	}
	
	// startup all registered component-extensions
	for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
	{
		extension->second->onStartup();
	}
	stateSlave->release("init");
	
	// do not call this handler within the init state (see above) as this handler internally calls setStartupFinished() (this should be fixed in future)
	compHandler.onStartup();
	
	// this call blocks until the component is commanded to shutdown
	stateSlave->acquire("shutdown");
	
	// shutdown all registered component-extensions
	for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
	{
		extension->second->onShutdown();
	}
	
	// shutdown all registered port-factories
	for(auto portFactory = portFactoryRegistry.begin(); portFactory != portFactoryRegistry.end(); portFactory++) 
	{
		portFactory->second->onShutdown();
	}
	
	if(connections.component.useLogger == true) {
		//FIXME: use logging
		//Smart::LOGGER->stopLogging();
	}
	
	compHandler.onShutdown();
	
	stateSlave->release("shutdown");
}

// clean-up component's resources
void EnforcerTemplate::fini()
{
	// unlink all observers
	
	// destroy all task instances
	// unlink all UpcallManagers
	enforcementReply_PushUpcallManager->detach(enforcementManager);
	enforcementReply_SendUpcallManager->detach(enforcementManager);
	enforcementReport_PushUpcallManager->detach(enforcementManager);
	enforcementReport_SendUpcallManager->detach(enforcementManager);
	// unlink the TaskTrigger
	if(enforcementManagerTrigger != NULL){
		enforcementManagerTrigger->detach(enforcementManager);
		delete enforcementManager;
	}
	// unlink all UpcallManagers
	event_ListenerUpcallManager->detach(eventManager);
	// unlink the TaskTrigger
	if(eventManagerTrigger != NULL){
		eventManagerTrigger->detach(eventManager);
		delete eventManager;
	}

	// destroy all input-handler

	// destroy InputTaskTriggers and UpcallManagers
	delete enforcementReply_PushInputTaskTrigger;
	delete enforcementReply_PushUpcallManager;
	delete enforcementReply_PushInputCollector;
	delete enforcementReply_SendInputTaskTrigger;
	delete enforcementReply_SendUpcallManager;
	delete enforcementReply_SendInputCollector;
	delete enforcementReport_PushInputTaskTrigger;
	delete enforcementReport_PushUpcallManager;
	delete enforcementReport_PushInputCollector;
	delete enforcementReport_SendInputTaskTrigger;
	delete enforcementReport_SendUpcallManager;
	delete enforcementReport_SendInputCollector;
	delete event_ListenerInputTaskTrigger;
	delete event_ListenerUpcallManager;
	delete event_ListenerInputCollector;

	// destroy client ports
	delete enforcementInstruction_SendWrapper;
	delete enforcementInstruction_Send;
	delete enforcementReply_Push;
	delete enforcementReport_Push;
	delete enforcement_QueryRequester;
	delete event_Listener;
	delete report_QueryRequester;

	// destroy server ports
	delete enforcementInstruction_PushWrapper;
	delete enforcementInstruction_Push;
	delete enforcementReply_Send;
	delete enforcementReport_Send;
	delete event_CreatorWrapper;
	delete event_Creator;
	// destroy event-test handlers (if needed)
	event_CreatorEventTestHandler;
	
	// destroy request-handlers
	
	delete stateSlave;
	// destroy state-change-handler
	delete stateChangeHandler;
	
	// destroy all master/slave ports
	delete wiringSlave;
	

	// destroy all registered component-extensions
	for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
	{
		extension->second->destroy();
	}

	// destroy all registered port-factories
	for(auto portFactory = portFactoryRegistry.begin(); portFactory != portFactoryRegistry.end(); portFactory++) 
	{
		portFactory->second->destroy();
	}
	
}

void EnforcerTemplate::loadParameter(int argc, char *argv[])
{
	/*
	 Parameters can be specified via command line --filename=<filename> or -f <filename>

	 With this parameter present:
	   - The component will look for the file in the current working directory,
	     a path relative to the current directory or any absolute path
	   - The component will use the default values if the file cannot be found

	 With this parameter absent:
	   - <Name of Component>.ini will be read from current working directory, if found there
	   - $SMART_ROOT/etc/<Name of Component>.ini will be read otherwise
	   - Default values will be used if neither found in working directory or /etc
	 */
	SmartACE::SmartIniParameter parameter;
	std::ifstream parameterfile;
	bool parameterFileFound = false;

	// load parameters
	try
	{
		// if paramfile is given as argument
		if(parameter.tryAddFileFromArgs(argc,argv,"filename", 'f'))
		{
			parameterFileFound = true;
			std::cout << "parameter file is loaded from an argv argument \n";
		} else if(parameter.searchFile("EnforcerTemplate.ini", parameterfile)) {
			parameterFileFound = true;
			std::cout << "load EnforcerTemplate.ini parameter file\n";
			parameter.addFile(parameterfile);
		} else {
			std::cout << "WARNING: EnforcerTemplate.ini parameter file not found! (using default values or command line arguments)\n";
		}
		
		// add command line arguments to allow overwriting of parameters
		// from file
		parameter.addCommandLineArgs(argc,argv,"component");
		
		// initialize the naming service using the command line parameters parsed in the
		// SmartIniParameter class. The naming service parameters are expected to be in
		// the "component" parameter group.
		SmartACE::NAMING::instance()->checkForHelpArg(argc,argv);
		if(parameterFileFound) 
		{
			if(SmartACE::NAMING::instance()->init(parameter.getAllParametersFromGroup("component")) != 0) {
				// initialization of naming service failed
				throw std::logic_error( "<NamingService> Service initialization failed!\nPossible causes could be:\n-> Erroneous configuration.\n-> Naming service not reachable.\n" );
			}
		} else {
			if(SmartACE::NAMING::instance()->init(argc, argv) != 0) {
				// initialization of naming service failed
				throw std::logic_error( "<NamingService> Service initialization failed!\nPossible causes could be:\n-> Erroneous configuration.\n-> Naming service not reachable.\n" );
			}
		}
			
		// print all known parameters
		// parameter.print();
		
		//--- server port // client port // other parameter ---
		// load parameter
		parameter.getString("component", "name", connections.component.name);
		parameter.getString("component", "initialComponentMode", connections.component.initialComponentMode);
		if(parameter.checkIfParameterExists("component", "defaultScheduler")) {
			parameter.getString("component", "defaultScheduler", connections.component.defaultScheduler);
		}
		if(parameter.checkIfParameterExists("component", "useLogger")) {
			parameter.getBoolean("component", "useLogger", connections.component.useLogger);
		}
		
		// load parameters for client EnforcementInstruction_Send
		parameter.getBoolean("EnforcementInstruction_Send", "initialConnect", connections.enforcementInstruction_Send.initialConnect);
		parameter.getString("EnforcementInstruction_Send", "serviceName", connections.enforcementInstruction_Send.serviceName);
		parameter.getString("EnforcementInstruction_Send", "serverName", connections.enforcementInstruction_Send.serverName);
		parameter.getString("EnforcementInstruction_Send", "wiringName", connections.enforcementInstruction_Send.wiringName);
		if(parameter.checkIfParameterExists("EnforcementInstruction_Send", "roboticMiddleware")) {
			parameter.getString("EnforcementInstruction_Send", "roboticMiddleware", connections.enforcementInstruction_Send.roboticMiddleware);
		}
		// load parameters for client EnforcementReply_Push
		parameter.getBoolean("EnforcementReply_Push", "initialConnect", connections.enforcementReply_Push.initialConnect);
		parameter.getString("EnforcementReply_Push", "serviceName", connections.enforcementReply_Push.serviceName);
		parameter.getString("EnforcementReply_Push", "serverName", connections.enforcementReply_Push.serverName);
		parameter.getString("EnforcementReply_Push", "wiringName", connections.enforcementReply_Push.wiringName);
		parameter.getInteger("EnforcementReply_Push", "interval", connections.enforcementReply_Push.interval);
		if(parameter.checkIfParameterExists("EnforcementReply_Push", "roboticMiddleware")) {
			parameter.getString("EnforcementReply_Push", "roboticMiddleware", connections.enforcementReply_Push.roboticMiddleware);
		}
		// load parameters for client EnforcementReport_Push
		parameter.getBoolean("EnforcementReport_Push", "initialConnect", connections.enforcementReport_Push.initialConnect);
		parameter.getString("EnforcementReport_Push", "serviceName", connections.enforcementReport_Push.serviceName);
		parameter.getString("EnforcementReport_Push", "serverName", connections.enforcementReport_Push.serverName);
		parameter.getString("EnforcementReport_Push", "wiringName", connections.enforcementReport_Push.wiringName);
		parameter.getInteger("EnforcementReport_Push", "interval", connections.enforcementReport_Push.interval);
		if(parameter.checkIfParameterExists("EnforcementReport_Push", "roboticMiddleware")) {
			parameter.getString("EnforcementReport_Push", "roboticMiddleware", connections.enforcementReport_Push.roboticMiddleware);
		}
		// load parameters for client Enforcement_QueryRequester
		parameter.getBoolean("Enforcement_QueryRequester", "initialConnect", connections.enforcement_QueryRequester.initialConnect);
		parameter.getString("Enforcement_QueryRequester", "serviceName", connections.enforcement_QueryRequester.serviceName);
		parameter.getString("Enforcement_QueryRequester", "serverName", connections.enforcement_QueryRequester.serverName);
		parameter.getString("Enforcement_QueryRequester", "wiringName", connections.enforcement_QueryRequester.wiringName);
		if(parameter.checkIfParameterExists("Enforcement_QueryRequester", "roboticMiddleware")) {
			parameter.getString("Enforcement_QueryRequester", "roboticMiddleware", connections.enforcement_QueryRequester.roboticMiddleware);
		}
		// load parameters for client Event_Listener
		parameter.getBoolean("Event_Listener", "initialConnect", connections.event_Listener.initialConnect);
		parameter.getString("Event_Listener", "serviceName", connections.event_Listener.serviceName);
		parameter.getString("Event_Listener", "serverName", connections.event_Listener.serverName);
		parameter.getString("Event_Listener", "wiringName", connections.event_Listener.wiringName);
		if(parameter.checkIfParameterExists("Event_Listener", "roboticMiddleware")) {
			parameter.getString("Event_Listener", "roboticMiddleware", connections.event_Listener.roboticMiddleware);
		}
		// load parameters for client Report_QueryRequester
		parameter.getBoolean("Report_QueryRequester", "initialConnect", connections.report_QueryRequester.initialConnect);
		parameter.getString("Report_QueryRequester", "serviceName", connections.report_QueryRequester.serviceName);
		parameter.getString("Report_QueryRequester", "serverName", connections.report_QueryRequester.serverName);
		parameter.getString("Report_QueryRequester", "wiringName", connections.report_QueryRequester.wiringName);
		if(parameter.checkIfParameterExists("Report_QueryRequester", "roboticMiddleware")) {
			parameter.getString("Report_QueryRequester", "roboticMiddleware", connections.report_QueryRequester.roboticMiddleware);
		}
		
		// load parameters for server EnforcementInstruction_Push
		parameter.getString("EnforcementInstruction_Push", "serviceName", connections.enforcementInstruction_Push.serviceName);
		if(parameter.checkIfParameterExists("EnforcementInstruction_Push", "roboticMiddleware")) {
			parameter.getString("EnforcementInstruction_Push", "roboticMiddleware", connections.enforcementInstruction_Push.roboticMiddleware);
		}
		// load parameters for server EnforcementReply_Send
		parameter.getString("EnforcementReply_Send", "serviceName", connections.enforcementReply_Send.serviceName);
		if(parameter.checkIfParameterExists("EnforcementReply_Send", "roboticMiddleware")) {
			parameter.getString("EnforcementReply_Send", "roboticMiddleware", connections.enforcementReply_Send.roboticMiddleware);
		}
		// load parameters for server EnforcementReport_Send
		parameter.getString("EnforcementReport_Send", "serviceName", connections.enforcementReport_Send.serviceName);
		if(parameter.checkIfParameterExists("EnforcementReport_Send", "roboticMiddleware")) {
			parameter.getString("EnforcementReport_Send", "roboticMiddleware", connections.enforcementReport_Send.roboticMiddleware);
		}
		// load parameters for server Event_Creator
		parameter.getString("Event_Creator", "serviceName", connections.event_Creator.serviceName);
		if(parameter.checkIfParameterExists("Event_Creator", "roboticMiddleware")) {
			parameter.getString("Event_Creator", "roboticMiddleware", connections.event_Creator.roboticMiddleware);
		}
		
		// load parameters for task EnforcementManager
		parameter.getDouble("EnforcementManager", "minActFreqHz", connections.enforcementManager.minActFreq);
		parameter.getDouble("EnforcementManager", "maxActFreqHz", connections.enforcementManager.maxActFreq);
		parameter.getString("EnforcementManager", "triggerType", connections.enforcementManager.trigger);
		if(connections.enforcementManager.trigger == "PeriodicTimer") {
			parameter.getDouble("EnforcementManager", "periodicActFreqHz", connections.enforcementManager.periodicActFreq);
		} else if(connections.enforcementManager.trigger == "DataTriggered") {
			parameter.getString("EnforcementManager", "inPortRef", connections.enforcementManager.inPortRef);
			parameter.getInteger("EnforcementManager", "prescale", connections.enforcementManager.prescale);
		}
		if(parameter.checkIfParameterExists("EnforcementManager", "scheduler")) {
			parameter.getString("EnforcementManager", "scheduler", connections.enforcementManager.scheduler);
		}
		if(parameter.checkIfParameterExists("EnforcementManager", "priority")) {
			parameter.getInteger("EnforcementManager", "priority", connections.enforcementManager.priority);
		}
		if(parameter.checkIfParameterExists("EnforcementManager", "cpuAffinity")) {
			parameter.getInteger("EnforcementManager", "cpuAffinity", connections.enforcementManager.cpuAffinity);
		}
		// load parameters for task EventManager
		parameter.getDouble("EventManager", "minActFreqHz", connections.eventManager.minActFreq);
		parameter.getDouble("EventManager", "maxActFreqHz", connections.eventManager.maxActFreq);
		parameter.getString("EventManager", "triggerType", connections.eventManager.trigger);
		if(connections.eventManager.trigger == "PeriodicTimer") {
			parameter.getDouble("EventManager", "periodicActFreqHz", connections.eventManager.periodicActFreq);
		} else if(connections.eventManager.trigger == "DataTriggered") {
			parameter.getString("EventManager", "inPortRef", connections.eventManager.inPortRef);
			parameter.getInteger("EventManager", "prescale", connections.eventManager.prescale);
		}
		if(parameter.checkIfParameterExists("EventManager", "scheduler")) {
			parameter.getString("EventManager", "scheduler", connections.eventManager.scheduler);
		}
		if(parameter.checkIfParameterExists("EventManager", "priority")) {
			parameter.getInteger("EventManager", "priority", connections.eventManager.priority);
		}
		if(parameter.checkIfParameterExists("EventManager", "cpuAffinity")) {
			parameter.getInteger("EventManager", "cpuAffinity", connections.eventManager.cpuAffinity);
		}
		
		
		// load parameters for all registered component-extensions
		for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
		{
			extension->second->loadParameters(parameter);
		}
		
	
	} catch (const SmartACE::IniParameterError & e) {
		std::cerr << e.what() << std::endl;
	} catch (const std::exception &ex) {
		std::cerr << "Uncaught std::exception: " << ex.what() << std::endl;
	} catch (...) {
		std::cerr << "Uncaught exception" << std::endl;
	}
}
