Smart::StatusCode SmartINJoystickServer::connectEnforcementReport_Send(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	if(connections.enforcementReport_Send.initialConnect == false) {
		return Smart::SMART_OK;
	}
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = enforcementReport_Send->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->enforcementReport_Send->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	return status;
}


