void SmartINJoystickServer::loadParameter(int argc, char *argv[])
{
	/*
	 Parameters can be specified via command line --filename=<filename> or -f <filename>

	 With this parameter present:
	   - The component will look for the file in the current working directory,
	     a path relative to the current directory or any absolute path
	   - The component will use the default values if the file cannot be found

	 With this parameter absent:
	   - <Name of Component>.ini will be read from current working directory, if found there
	   - $SMART_ROOT/etc/<Name of Component>.ini will be read otherwise
	   - Default values will be used if neither found in working directory or /etc
	 */
	SmartACE::SmartIniParameter parameter;
	std::ifstream parameterfile;
	bool parameterFileFound = false;

	// load parameters
	try
	{
		// if paramfile is given as argument
		if(parameter.tryAddFileFromArgs(argc,argv,"filename", 'f'))
		{
			parameterFileFound = true;
			std::cout << "parameter file is loaded from an argv argument \n";
		} else if(parameter.searchFile("SmartINJoystickServer.ini", parameterfile)) {
			parameterFileFound = true;
			std::cout << "load SmartINJoystickServer.ini parameter file\n";
			parameter.addFile(parameterfile);
		} else {
			std::cout << "WARNING: SmartINJoystickServer.ini parameter file not found! (using default values or command line arguments)\n";
		}
		
		// add command line arguments to allow overwriting of parameters
		// from file
		parameter.addCommandLineArgs(argc,argv,"component");
		
		// initialize the naming service using the command line parameters parsed in the
		// SmartIniParameter class. The naming service parameters are expected to be in
		// the "component" parameter group.
		SmartACE::NAMING::instance()->checkForHelpArg(argc,argv);
		if(parameterFileFound) 
		{
			if(SmartACE::NAMING::instance()->init(parameter.getAllParametersFromGroup("component")) != 0) {
				// initialization of naming service failed
				throw std::logic_error( "<NamingService> Service initialization failed!\nPossible causes could be:\n-> Erroneous configuration.\n-> Naming service not reachable.\n" );
			}
		} else {
			if(SmartACE::NAMING::instance()->init(argc, argv) != 0) {
				// initialization of naming service failed
				throw std::logic_error( "<NamingService> Service initialization failed!\nPossible causes could be:\n-> Erroneous configuration.\n-> Naming service not reachable.\n" );
			}
		}
			
		// print all known parameters
		// parameter.print();
		
		//--- server port // client port // other parameter ---
		// load parameter
		parameter.getString("component", "name", connections.component.name);
		parameter.getString("component", "initialComponentMode", connections.component.initialComponentMode);
		if(parameter.checkIfParameterExists("component", "defaultScheduler")) {
			parameter.getString("component", "defaultScheduler", connections.component.defaultScheduler);
		}
		if(parameter.checkIfParameterExists("component", "useLogger")) {
			parameter.getBoolean("component", "useLogger", connections.component.useLogger);
		}
		
		// load parameters for client EnforcementReport_Send
		parameter.getBoolean("EnforcementReport_Send", "initialConnect", connections.enforcementReport_Send.initialConnect);
		parameter.getString("EnforcementReport_Send", "serviceName", connections.enforcementReport_Send.serviceName);
		parameter.getString("EnforcementReport_Send", "serverName", connections.enforcementReport_Send.serverName);
		parameter.getString("EnforcementReport_Send", "wiringName", connections.enforcementReport_Send.wiringName);
		if(parameter.checkIfParameterExists("EnforcementReport_Send", "roboticMiddleware")) {
			parameter.getString("EnforcementReport_Send", "roboticMiddleware", connections.enforcementReport_Send.roboticMiddleware);
		}
		
		// load parameters for server JoystickServcieOut
		parameter.getString("JoystickServcieOut", "serviceName", connections.joystickServcieOut.serviceName);
		if(parameter.checkIfParameterExists("JoystickServcieOut", "roboticMiddleware")) {
			parameter.getString("JoystickServcieOut", "roboticMiddleware", connections.joystickServcieOut.roboticMiddleware);
		}
		
		// load parameters for task ComplianceManager
		parameter.getDouble("ComplianceManager", "minActFreqHz", connections.complianceManager.minActFreq);
		parameter.getDouble("ComplianceManager", "maxActFreqHz", connections.complianceManager.maxActFreq);
		parameter.getString("ComplianceManager", "triggerType", connections.complianceManager.trigger);
		if(connections.complianceManager.trigger == "PeriodicTimer") {
			parameter.getDouble("ComplianceManager", "periodicActFreqHz", connections.complianceManager.periodicActFreq);
		} else if(connections.complianceManager.trigger == "DataTriggered") {
			parameter.getString("ComplianceManager", "inPortRef", connections.complianceManager.inPortRef);
			parameter.getInteger("ComplianceManager", "prescale", connections.complianceManager.prescale);
		}
		if(parameter.checkIfParameterExists("ComplianceManager", "scheduler")) {
			parameter.getString("ComplianceManager", "scheduler", connections.complianceManager.scheduler);
		}
		if(parameter.checkIfParameterExists("ComplianceManager", "priority")) {
			parameter.getInteger("ComplianceManager", "priority", connections.complianceManager.priority);
		}
		if(parameter.checkIfParameterExists("ComplianceManager", "cpuAffinity")) {
			parameter.getInteger("ComplianceManager", "cpuAffinity", connections.complianceManager.cpuAffinity);
		}
		// load parameters for task JoystickTask
		if(parameter.checkIfParameterExists("JoystickTask", "scheduler")) {
			parameter.getString("JoystickTask", "scheduler", connections.joystickTask.scheduler);
		}
		if(parameter.checkIfParameterExists("JoystickTask", "priority")) {
			parameter.getInteger("JoystickTask", "priority", connections.joystickTask.priority);
		}
		if(parameter.checkIfParameterExists("JoystickTask", "cpuAffinity")) {
			parameter.getInteger("JoystickTask", "cpuAffinity", connections.joystickTask.cpuAffinity);
		}
		
		
		// load parameters for all registered component-extensions
		for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
		{
			extension->second->loadParameters(parameter);
		}
		
		paramHandler.loadParameter(parameter);
	
	} catch (const SmartACE::IniParameterError & e) {
		std::cerr << e.what() << std::endl;
	} catch (const std::exception &ex) {
		std::cerr << "Uncaught std::exception: " << ex.what() << std::endl;
	} catch (...) {
		std::cerr << "Uncaught exception" << std::endl;
	}
}
