Smart::StatusCode SmartJoystickManager::connectJoystickServiceIn_1(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	if(connections.joystickServiceIn_1.initialConnect == false) {
		return Smart::SMART_OK;
	}
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = joystickServiceIn_1->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->joystickServiceIn_1->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	joystickServiceIn_1->subscribe(connections.joystickServiceIn_1.interval);
	return status;
}
