/**
 * Start all tasks contained in this component.
 */
void SmartJoystickManager::startAllTasks() {
	// start task JoystickManTask
	if(connections.joystickManTask.scheduler != "DEFAULT") {
		ACE_Sched_Params joystickManTask_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.joystickManTask.scheduler == "FIFO") {
			joystickManTask_SchedParams.policy(ACE_SCHED_FIFO);
			joystickManTask_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.joystickManTask.scheduler == "RR") {
			joystickManTask_SchedParams.policy(ACE_SCHED_RR);
			joystickManTask_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		joystickManTask->start(joystickManTask_SchedParams, connections.joystickManTask.cpuAffinity);
	} else {
		joystickManTask->start();
	}
}

