void SmartJoystickManager::init(int argc, char *argv[])
{
	try {
		Smart::StatusCode status;
		
		// load initial parameters from ini-file (if found)
		loadParameter(argc, argv);
		
		
		
		// initialize all registered port-factories
		for(auto portFactory = portFactoryRegistry.begin(); portFactory != portFactoryRegistry.end(); portFactory++) 
		{
			portFactory->second->initialize(this, argc, argv);
		}
		
		// initialize all registered component-extensions
		for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
		{
			extension->second->initialize(this, argc, argv);
		}
		
		SmartJoystickManagerPortFactoryInterface *acePortFactory = portFactoryRegistry["ACE_SmartSoft"];
		if(acePortFactory == 0) {
			std::cerr << "ERROR: acePortFactory NOT instantiated -> exit(-1)" << std::endl;
			exit(-1);
		}
		
		// this pointer is used for backwards compatibility (deprecated: should be removed as soon as all patterns, including coordination, are moved to port-factory)
		SmartACE::SmartComponent *component = dynamic_cast<SmartJoystickManagerAcePortFactory*>(acePortFactory)->getComponentImpl();
		
		std::cout << "ComponentDefinition SmartJoystickManager is named " << connections.component.name << std::endl;
		
		if(connections.component.useLogger == true) {
			//FIXME: use logging
			//Smart::LOGGER->openLogFileInFolder("data/"+connections.component.name);
			//Smart::LOGGER->startLogging();
		}

		// create event-test handlers (if needed)
		
		// create server ports
		// TODO: set minCycleTime from Ini-file
		joystickServiceOut = portFactoryRegistry[connections.joystickServiceOut.roboticMiddleware]->createJoystickServiceOut(connections.joystickServiceOut.serviceName);
		joystickServiceOutWrapper = new JoystickServiceOutWrapper(joystickServiceOut);
		
		// create client ports
		joystickServiceIn_1 = portFactoryRegistry[connections.joystickServiceIn_1.roboticMiddleware]->createJoystickServiceIn_1();
		joystickServiceIn_2 = portFactoryRegistry[connections.joystickServiceIn_2.roboticMiddleware]->createJoystickServiceIn_2();
		joystickServiceIn_3 = portFactoryRegistry[connections.joystickServiceIn_3.roboticMiddleware]->createJoystickServiceIn_3();
		joystickServiceIn_4 = portFactoryRegistry[connections.joystickServiceIn_4.roboticMiddleware]->createJoystickServiceIn_4();
		joystickServiceIn_5 = portFactoryRegistry[connections.joystickServiceIn_5.roboticMiddleware]->createJoystickServiceIn_5();
		
		// create InputTaskTriggers and UpcallManagers
		joystickServiceIn_1InputCollector = new JoystickServiceIn_1InputCollector(joystickServiceIn_1);
		joystickServiceIn_1InputTaskTrigger = new Smart::InputTaskTrigger<CommBasicObjects::CommJoystick>(joystickServiceIn_1InputCollector);
		joystickServiceIn_1UpcallManager = new JoystickServiceIn_1UpcallManager(joystickServiceIn_1InputCollector);
		joystickServiceIn_2InputCollector = new JoystickServiceIn_2InputCollector(joystickServiceIn_2);
		joystickServiceIn_2InputTaskTrigger = new Smart::InputTaskTrigger<CommBasicObjects::CommJoystick>(joystickServiceIn_2InputCollector);
		joystickServiceIn_2UpcallManager = new JoystickServiceIn_2UpcallManager(joystickServiceIn_2InputCollector);
		joystickServiceIn_3InputCollector = new JoystickServiceIn_3InputCollector(joystickServiceIn_3);
		joystickServiceIn_3InputTaskTrigger = new Smart::InputTaskTrigger<CommBasicObjects::CommJoystick>(joystickServiceIn_3InputCollector);
		joystickServiceIn_3UpcallManager = new JoystickServiceIn_3UpcallManager(joystickServiceIn_3InputCollector);
		joystickServiceIn_4InputCollector = new JoystickServiceIn_4InputCollector(joystickServiceIn_4);
		joystickServiceIn_4InputTaskTrigger = new Smart::InputTaskTrigger<CommBasicObjects::CommJoystick>(joystickServiceIn_4InputCollector);
		joystickServiceIn_4UpcallManager = new JoystickServiceIn_4UpcallManager(joystickServiceIn_4InputCollector);
		joystickServiceIn_5InputCollector = new JoystickServiceIn_5InputCollector(joystickServiceIn_5);
		joystickServiceIn_5InputTaskTrigger = new Smart::InputTaskTrigger<CommBasicObjects::CommJoystick>(joystickServiceIn_5InputCollector);
		joystickServiceIn_5UpcallManager = new JoystickServiceIn_5UpcallManager(joystickServiceIn_5InputCollector);
		
		// create input-handler
		
		// create request-handlers
		
		// create state pattern
		stateChangeHandler = new SmartStateChangeHandler();
		stateSlave = new SmartACE::StateSlave(component, stateChangeHandler);
		status = stateSlave->setUpInitialState(connections.component.initialComponentMode);
		if (status != Smart::SMART_OK) std::cerr << status << "; failed setting initial ComponentMode: " << connections.component.initialComponentMode << std::endl;
		// activate state slave
		status = stateSlave->activate();
		if(status != Smart::SMART_OK) std::cerr << "ERROR: activate state" << std::endl;
		
		wiringSlave = new SmartACE::WiringSlave(component);
		// add client port to wiring slave
		if(connections.joystickServiceIn_1.roboticMiddleware == "ACE_SmartSoft") {
			//FIXME: this must also work with other implementations
			dynamic_cast<SmartACE::PushClient<CommBasicObjects::CommJoystick>*>(joystickServiceIn_1)->add(wiringSlave, connections.joystickServiceIn_1.wiringName);
		}
		if(connections.joystickServiceIn_2.roboticMiddleware == "ACE_SmartSoft") {
			//FIXME: this must also work with other implementations
			dynamic_cast<SmartACE::PushClient<CommBasicObjects::CommJoystick>*>(joystickServiceIn_2)->add(wiringSlave, connections.joystickServiceIn_2.wiringName);
		}
		if(connections.joystickServiceIn_3.roboticMiddleware == "ACE_SmartSoft") {
			//FIXME: this must also work with other implementations
			dynamic_cast<SmartACE::PushClient<CommBasicObjects::CommJoystick>*>(joystickServiceIn_3)->add(wiringSlave, connections.joystickServiceIn_3.wiringName);
		}
		if(connections.joystickServiceIn_4.roboticMiddleware == "ACE_SmartSoft") {
			//FIXME: this must also work with other implementations
			dynamic_cast<SmartACE::PushClient<CommBasicObjects::CommJoystick>*>(joystickServiceIn_4)->add(wiringSlave, connections.joystickServiceIn_4.wiringName);
		}
		if(connections.joystickServiceIn_5.roboticMiddleware == "ACE_SmartSoft") {
			//FIXME: this must also work with other implementations
			dynamic_cast<SmartACE::PushClient<CommBasicObjects::CommJoystick>*>(joystickServiceIn_5)->add(wiringSlave, connections.joystickServiceIn_5.wiringName);
		}
		
		
		
		// create Task JoystickManTask
		joystickManTask = new JoystickManTask(component);
		// configure input-links
		joystickServiceIn_1UpcallManager->attach(joystickManTask);
		joystickServiceIn_2UpcallManager->attach(joystickManTask);
		joystickServiceIn_3UpcallManager->attach(joystickManTask);
		joystickServiceIn_4UpcallManager->attach(joystickManTask);
		joystickServiceIn_5UpcallManager->attach(joystickManTask);
		// configure task-trigger (if task is configurable)
		if(connections.joystickManTask.trigger == "PeriodicTimer") {
			// create PeriodicTimerTrigger
			int microseconds = 1000*1000 / connections.joystickManTask.periodicActFreq;
			if(microseconds > 0) {
				Smart::TimedTaskTrigger *triggerPtr = new Smart::TimedTaskTrigger();
				triggerPtr->attach(joystickManTask);
				component->getTimerManager()->scheduleTimer(triggerPtr, (void *) 0, std::chrono::microseconds(microseconds), std::chrono::microseconds(microseconds));
				// store trigger in class member
				joystickManTaskTrigger = triggerPtr;
			} else {
				std::cerr << "ERROR: could not set-up Timer with cycle-time " << microseconds << " as activation source for Task JoystickManTask" << std::endl;
			}
		} else if(connections.joystickManTask.trigger == "DataTriggered") {
			joystickManTaskTrigger = getInputTaskTriggerFromString(connections.joystickManTask.inPortRef);
			if(joystickManTaskTrigger != NULL) {
				joystickManTaskTrigger->attach(joystickManTask, connections.joystickManTask.prescale);
			} else {
				std::cerr << "ERROR: could not set-up InPort " << connections.joystickManTask.inPortRef << " as activation source for Task JoystickManTask" << std::endl;
			}
		} 
		
		
		// link observers with subjects
	} catch (const std::exception &ex) {
		std::cerr << "Uncaught std exception" << ex.what() << std::endl;
	} catch (...) {
		std::cerr << "Uncaught exception" << std::endl;
	}
}

// run the component
void SmartJoystickManager::run()
{
	stateSlave->acquire("init");
	// startup all registered port-factories
	for(auto portFactory = portFactoryRegistry.begin(); portFactory != portFactoryRegistry.end(); portFactory++) 
	{
		portFactory->second->onStartup();
	}
	
	// startup all registered component-extensions
	for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
	{
		extension->second->onStartup();
	}
	stateSlave->release("init");
	
	// do not call this handler within the init state (see above) as this handler internally calls setStartupFinished() (this should be fixed in future)
	compHandler.onStartup();
	
	// this call blocks until the component is commanded to shutdown
	stateSlave->acquire("shutdown");
	
	// shutdown all registered component-extensions
	for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
	{
		extension->second->onShutdown();
	}
	
	// shutdown all registered port-factories
	for(auto portFactory = portFactoryRegistry.begin(); portFactory != portFactoryRegistry.end(); portFactory++) 
	{
		portFactory->second->onShutdown();
	}
	
	if(connections.component.useLogger == true) {
		//FIXME: use logging
		//Smart::LOGGER->stopLogging();
	}
	
	compHandler.onShutdown();
	
	stateSlave->release("shutdown");
}

