	// destroy all task instances
	// unlink all UpcallManagers
	joystickServiceIn_1UpcallManager->detach(joystickManTask);
	joystickServiceIn_2UpcallManager->detach(joystickManTask);
	joystickServiceIn_3UpcallManager->detach(joystickManTask);
