	void CMatrixDouble22Core::to_ostream(std::ostream &os) const
	{
	  os << "CMatrixDouble22(";
	  os << getM_00() << " ";
	  os << getM_01() << " ";
	  os << getM_10() << " ";
	  os << getM_11() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CMatrixDouble22Core::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<m_00>" << getM_00() << "</m_00>";
		os << indent << "<m_01>" << getM_01() << "</m_01>";
		os << indent << "<m_10>" << getM_10() << "</m_10>";
		os << indent << "<m_11>" << getM_11() << "</m_11>";
	}
	
	// restore from xml stream
	void CMatrixDouble22Core::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_m_00("<m_00>");
		static const Smart::KnuthMorrisPratt kmp_m_01("<m_01>");
		static const Smart::KnuthMorrisPratt kmp_m_10("<m_10>");
		static const Smart::KnuthMorrisPratt kmp_m_11("<m_11>");
		
		if(kmp_m_00.search(is)) {
			double m_00Item;
			is >> m_00Item;
			setM_00(m_00Item);
		}
		if(kmp_m_01.search(is)) {
			double m_01Item;
			is >> m_01Item;
			setM_01(m_01Item);
		}
		if(kmp_m_10.search(is)) {
			double m_10Item;
			is >> m_10Item;
			setM_10(m_10Item);
		}
		if(kmp_m_11.search(is)) {
			double m_11Item;
			is >> m_11Item;
			setM_11(m_11Item);
		}
	}
	
