	const char* CPose2DCore::getCompiledHash()
	{
		return CommLocalizationObjectsIDL::REPO_HASH;
	}
	
	void CPose2DCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
	}
	
	void CPose2DCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "CPose2DCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
	}
	
	#ifdef ENABLE_HASH
	size_t CPose2DCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.m_phi);
		boost::hash_combine(seed, data.m_is3D);
		boost::hash_combine(seed, data.m_x);
		boost::hash_combine(seed, data.m_y);
		boost::hash_combine(seed, data.m_z);
		
		return seed;
	}
	#endif
	
	// default constructor
	CPose2DCore::CPose2DCore()
	:	idl_CPose2D()
	{  
		setM_phi(0.0);
		setM_is3D(false);
		setM_x(0.0);
		setM_y(0.0);
		setM_z(0.0);
	}
	
	CPose2DCore::CPose2DCore(const DATATYPE &data)
	:	idl_CPose2D(data)
	{  }

	CPose2DCore::~CPose2DCore()
	{  }
	
	void CPose2DCore::to_ostream(std::ostream &os) const
	{
	  os << "CPose2D(";
	  os << getM_phi() << " ";
	  os << getM_is3D() << " ";
	  os << getM_x() << " ";
	  os << getM_y() << " ";
	  os << getM_z() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CPose2DCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<m_phi>" << getM_phi() << "</m_phi>";
		os << indent << "<m_is3D>" << getM_is3D() << "</m_is3D>";
		os << indent << "<m_x>" << getM_x() << "</m_x>";
		os << indent << "<m_y>" << getM_y() << "</m_y>";
		os << indent << "<m_z>" << getM_z() << "</m_z>";
	}
	
	// restore from xml stream
	void CPose2DCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_m_phi("<m_phi>");
		static const Smart::KnuthMorrisPratt kmp_m_is3D("<m_is3D>");
		static const Smart::KnuthMorrisPratt kmp_m_x("<m_x>");
		static const Smart::KnuthMorrisPratt kmp_m_y("<m_y>");
		static const Smart::KnuthMorrisPratt kmp_m_z("<m_z>");
		
		if(kmp_m_phi.search(is)) {
			double m_phiItem;
			is >> m_phiItem;
			setM_phi(m_phiItem);
		}
		if(kmp_m_is3D.search(is)) {
			bool m_is3DItem;
			is >> m_is3DItem;
			setM_is3D(m_is3DItem);
		}
		if(kmp_m_x.search(is)) {
			double m_xItem;
			is >> m_xItem;
			setM_x(m_xItem);
		}
		if(kmp_m_y.search(is)) {
			double m_yItem;
			is >> m_yItem;
			setM_y(m_yItem);
		}
		if(kmp_m_z.search(is)) {
			double m_zItem;
			is >> m_zItem;
			setM_z(m_zItem);
		}
	}
	
