	void CommAmclVisualizationInfoCore::to_ostream(std::ostream &os) const
	{
	  os << "CommAmclVisualizationInfo(";
	  std::vector<CommLocalizationObjects::CommAmclParticle>::const_iterator particlesIt;
	  std::vector<CommLocalizationObjects::CommAmclParticle> particlesList = getParticlesCopy();
	  for(particlesIt=particlesList.begin(); particlesIt!=particlesList.end(); particlesIt++) {
	  	particlesIt->to_ostream(os);
	  }
	  std::vector<CommLocalizationObjects::CommAmclHypothesis>::const_iterator hypothesesIt;
	  std::vector<CommLocalizationObjects::CommAmclHypothesis> hypothesesList = getHypothesesCopy();
	  for(hypothesesIt=hypothesesList.begin(); hypothesesIt!=hypothesesList.end(); hypothesesIt++) {
	  	hypothesesIt->to_ostream(os);
	  }
	  os << ") ";
	}
	
	// convert to xml stream
	void CommAmclVisualizationInfoCore::to_xml(std::ostream &os, const std::string &indent) const {
		size_t counter = 0;
		
		std::vector<CommLocalizationObjects::CommAmclParticle>::const_iterator particlesIt;
		std::vector<CommLocalizationObjects::CommAmclParticle> particlesList = getParticlesCopy();
		counter = 0;
		os << indent << "<particlesList n=\"" << particlesList.size() << "\">";
		for(particlesIt=particlesList.begin(); particlesIt!=particlesList.end(); particlesIt++) {
			os << indent << "<particles i=\"" << counter++ << "\">";
			particlesIt->to_xml(os, indent);
			os << indent << "</particles>";
		}
		os << indent << "</particlesList>";
		std::vector<CommLocalizationObjects::CommAmclHypothesis>::const_iterator hypothesesIt;
		std::vector<CommLocalizationObjects::CommAmclHypothesis> hypothesesList = getHypothesesCopy();
		counter = 0;
		os << indent << "<hypothesesList n=\"" << hypothesesList.size() << "\">";
		for(hypothesesIt=hypothesesList.begin(); hypothesesIt!=hypothesesList.end(); hypothesesIt++) {
			os << indent << "<hypotheses i=\"" << counter++ << "\">";
			hypothesesIt->to_xml(os, indent);
			os << indent << "</hypotheses>";
		}
		os << indent << "</hypothesesList>";
	}
	
	// restore from xml stream
	void CommAmclVisualizationInfoCore::from_xml(std::istream &is) {
		size_t counter = 0;
		
		static const Smart::KnuthMorrisPratt kmp_particlesList("<particlesList n=\"");
		static const Smart::KnuthMorrisPratt kmp_particles("\">");
		static const Smart::KnuthMorrisPratt kmp_hypothesesList("<hypothesesList n=\"");
		static const Smart::KnuthMorrisPratt kmp_hypotheses("\">");
		
		if(kmp_particlesList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			CommLocalizationObjects::CommAmclParticle particlesItem;
			std::vector<CommLocalizationObjects::CommAmclParticle> particlesList;
			kmp_particles.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_particles.search(is)) {
					particlesItem.from_xml(is);
					particlesList.push_back(particlesItem);
				}
			}
			setParticles(particlesList);
		}
		if(kmp_hypothesesList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			CommLocalizationObjects::CommAmclHypothesis hypothesesItem;
			std::vector<CommLocalizationObjects::CommAmclHypothesis> hypothesesList;
			kmp_hypotheses.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_hypotheses.search(is)) {
					hypothesesItem.from_xml(is);
					hypothesesList.push_back(hypothesesItem);
				}
			}
			setHypotheses(hypothesesList);
		}
	}
	
