	void CommBeaconMeasurementCore::to_ostream(std::ostream &os) const
	{
	  os << "CommBeaconMeasurement(";
	  getTimeStamp().to_ostream(os);
	  os << getUuid() << " ";
	  os << getMajor() << " ";
	  os << getMinor() << " ";
	  os << getRssi() << " ";
	  os << getTxPower() << " ";
	  os << getDistance() << " ";
	  os << getDistanceIsValid() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommBeaconMeasurementCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<timeStamp>";
		getTimeStamp().to_xml(os, indent);
		os << indent << "</timeStamp>";
		os << indent << "<uuid>" << getUuid() << "</uuid>";
		os << indent << "<major>" << getMajor() << "</major>";
		os << indent << "<minor>" << getMinor() << "</minor>";
		os << indent << "<rssi>" << getRssi() << "</rssi>";
		os << indent << "<txPower>" << getTxPower() << "</txPower>";
		os << indent << "<distance>" << getDistance() << "</distance>";
		os << indent << "<distanceIsValid>" << getDistanceIsValid() << "</distanceIsValid>";
	}
	
	// restore from xml stream
	void CommBeaconMeasurementCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_timeStamp("<timeStamp>");
		static const Smart::KnuthMorrisPratt kmp_uuid("<uuid>");
		static const Smart::KnuthMorrisPratt kmp_major("<major>");
		static const Smart::KnuthMorrisPratt kmp_minor("<minor>");
		static const Smart::KnuthMorrisPratt kmp_rssi("<rssi>");
		static const Smart::KnuthMorrisPratt kmp_txPower("<txPower>");
		static const Smart::KnuthMorrisPratt kmp_distance("<distance>");
		static const Smart::KnuthMorrisPratt kmp_distanceIsValid("<distanceIsValid>");
		
		if(kmp_timeStamp.search(is)) {
			CommBasicObjects::CommTimeStamp timeStampItem;
			timeStampItem.from_xml(is);
			setTimeStamp(timeStampItem);
		}
		if(kmp_uuid.search(is)) {
			std::string uuidItem;
			is >> uuidItem;
			setUuid(uuidItem);
		}
		if(kmp_major.search(is)) {
			int majorItem;
			is >> majorItem;
			setMajor(majorItem);
		}
		if(kmp_minor.search(is)) {
			int minorItem;
			is >> minorItem;
			setMinor(minorItem);
		}
		if(kmp_rssi.search(is)) {
			float rssiItem;
			is >> rssiItem;
			setRssi(rssiItem);
		}
		if(kmp_txPower.search(is)) {
			float txPowerItem;
			is >> txPowerItem;
			setTxPower(txPowerItem);
		}
		if(kmp_distance.search(is)) {
			float distanceItem;
			is >> distanceItem;
			setDistance(distanceItem);
		}
		if(kmp_distanceIsValid.search(is)) {
			bool distanceIsValidItem;
			is >> distanceIsValidItem;
			setDistanceIsValid(distanceIsValidItem);
		}
	}
	
