	// convert to xml stream
	void CommNavigationTopologyMapCore::to_xml(std::ostream &os, const std::string &indent) const {
		size_t counter = 0;
		
		os << indent << "<isValid>" << getIsValid() << "</isValid>";
		std::vector<CommNavigationObjects::CommNavigationTopologyPart>::const_iterator verticesIt;
		std::vector<CommNavigationObjects::CommNavigationTopologyPart> verticesList = getVerticesCopy();
		counter = 0;
		os << indent << "<verticesList n=\"" << verticesList.size() << "\">";
		for(verticesIt=verticesList.begin(); verticesIt!=verticesList.end(); verticesIt++) {
			os << indent << "<vertices i=\"" << counter++ << "\">";
			verticesIt->to_xml(os, indent);
			os << indent << "</vertices>";
		}
		os << indent << "</verticesList>";
		std::vector<CommNavigationObjects::CommNavigationTopologyConnection>::const_iterator edgesIt;
		std::vector<CommNavigationObjects::CommNavigationTopologyConnection> edgesList = getEdgesCopy();
		counter = 0;
		os << indent << "<edgesList n=\"" << edgesList.size() << "\">";
		for(edgesIt=edgesList.begin(); edgesIt!=edgesList.end(); edgesIt++) {
			os << indent << "<edges i=\"" << counter++ << "\">";
			edgesIt->to_xml(os, indent);
			os << indent << "</edges>";
		}
		os << indent << "</edgesList>";
	}
	
