	void CommPlannerGoalCore::to_ostream(std::ostream &os) const
	{
	  os << "CommPlannerGoal(";
	  os << getId() << " ";
	  os << getFlag() << " ";
	  os << getXWayPoint() << " ";
	  os << getYWayPoint() << " ";
	  os << getHWayPoint() << " ";
	  os << getXGoalPoint() << " ";
	  os << getYGoalPoint() << " ";
	  os << getHGoalPoint() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommPlannerGoalCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<id>" << getId() << "</id>";
		os << indent << "<flag>" << getFlag() << "</flag>";
		os << indent << "<xWayPoint>" << getXWayPoint() << "</xWayPoint>";
		os << indent << "<yWayPoint>" << getYWayPoint() << "</yWayPoint>";
		os << indent << "<hWayPoint>" << getHWayPoint() << "</hWayPoint>";
		os << indent << "<xGoalPoint>" << getXGoalPoint() << "</xGoalPoint>";
		os << indent << "<yGoalPoint>" << getYGoalPoint() << "</yGoalPoint>";
		os << indent << "<hGoalPoint>" << getHGoalPoint() << "</hGoalPoint>";
	}
	
	// restore from xml stream
	void CommPlannerGoalCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_id("<id>");
		static const Smart::KnuthMorrisPratt kmp_flag("<flag>");
		static const Smart::KnuthMorrisPratt kmp_xWayPoint("<xWayPoint>");
		static const Smart::KnuthMorrisPratt kmp_yWayPoint("<yWayPoint>");
		static const Smart::KnuthMorrisPratt kmp_hWayPoint("<hWayPoint>");
		static const Smart::KnuthMorrisPratt kmp_xGoalPoint("<xGoalPoint>");
		static const Smart::KnuthMorrisPratt kmp_yGoalPoint("<yGoalPoint>");
		static const Smart::KnuthMorrisPratt kmp_hGoalPoint("<hGoalPoint>");
		
		if(kmp_id.search(is)) {
			int idItem;
			is >> idItem;
			setId(idItem);
		}
		if(kmp_flag.search(is)) {
			int flagItem;
			is >> flagItem;
			setFlag(flagItem);
		}
		if(kmp_xWayPoint.search(is)) {
			double xWayPointItem;
			is >> xWayPointItem;
			setXWayPoint(xWayPointItem);
		}
		if(kmp_yWayPoint.search(is)) {
			double yWayPointItem;
			is >> yWayPointItem;
			setYWayPoint(yWayPointItem);
		}
		if(kmp_hWayPoint.search(is)) {
			double hWayPointItem;
			is >> hWayPointItem;
			setHWayPoint(hWayPointItem);
		}
		if(kmp_xGoalPoint.search(is)) {
			double xGoalPointItem;
			is >> xGoalPointItem;
			setXGoalPoint(xGoalPointItem);
		}
		if(kmp_yGoalPoint.search(is)) {
			double yGoalPointItem;
			is >> yGoalPointItem;
			setYGoalPoint(yGoalPointItem);
		}
		if(kmp_hGoalPoint.search(is)) {
			double hGoalPointItem;
			is >> hGoalPointItem;
			setHGoalPoint(hGoalPointItem);
		}
	}
	
