	void SmartIN_CommandCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "SmartIN_CommandCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
		// check hash value(s) for CommBasicObjects::CommTaskMessage(idl_SmartIN_Command.msg)
		CommBasicObjects::CommTaskMessage::checkAllHashValues(hashes);
	}
	
	#ifdef ENABLE_HASH
	size_t SmartIN_CommandCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		seed += CommBasicObjects::CommTaskMessage::generateDataHash(data.msg);
		boost::hash_combine(seed, data.commandID);
		boost::hash_combine(seed, std::string(data.command.c_str()));
		
		return seed;
	}
	#endif
	
