	void SmartIN_CommandCore::to_ostream(std::ostream &os) const
	{
	  os << "SmartIN_Command(";
	  getMsg().to_ostream(os);
	  os << getCommandID() << " ";
	  os << getCommand() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void SmartIN_CommandCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<msg>";
		getMsg().to_xml(os, indent);
		os << indent << "</msg>";
		os << indent << "<commandID>" << getCommandID() << "</commandID>";
		os << indent << "<command>" << getCommand() << "</command>";
	}
	
	// restore from xml stream
	void SmartIN_CommandCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_msg("<msg>");
		static const Smart::KnuthMorrisPratt kmp_commandID("<commandID>");
		static const Smart::KnuthMorrisPratt kmp_command("<command>");
		
		if(kmp_msg.search(is)) {
			CommBasicObjects::CommTaskMessage msgItem;
			msgItem.from_xml(is);
			setMsg(msgItem);
		}
		if(kmp_commandID.search(is)) {
			unsigned long int commandIDItem;
			is >> commandIDItem;
			setCommandID(commandIDItem);
		}
		if(kmp_command.search(is)) {
			std::string commandItem;
			is >> commandItem;
			setCommand(commandItem);
		}
	}
	
