	void EnforcementReportPackageCore::to_ostream(std::ostream &os) const
	{
	  os << "EnforcementReportPackage(";
	  getId().to_ostream(os);
	  std::vector<SmartInstitutionsServiceRepository::MemberIdentifier>::const_iterator toIt;
	  std::vector<SmartInstitutionsServiceRepository::MemberIdentifier> toList = getToCopy();
	  for(toIt=toList.begin(); toIt!=toList.end(); toIt++) {
	  	toIt->to_ostream(os);
	  }
	  getFrom().to_ostream(os);
	  getReport().to_ostream(os);
	  os << getText() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void EnforcementReportPackageCore::to_xml(std::ostream &os, const std::string &indent) const {
		size_t counter = 0;
		
		os << indent << "<id>";
		getId().to_xml(os, indent);
		os << indent << "</id>";
		std::vector<SmartInstitutionsServiceRepository::MemberIdentifier>::const_iterator toIt;
		std::vector<SmartInstitutionsServiceRepository::MemberIdentifier> toList = getToCopy();
		counter = 0;
		os << indent << "<toList n=\"" << toList.size() << "\">";
		for(toIt=toList.begin(); toIt!=toList.end(); toIt++) {
			os << indent << "<to i=\"" << counter++ << "\">";
			toIt->to_xml(os, indent);
			os << indent << "</to>";
		}
		os << indent << "</toList>";
		os << indent << "<from>";
		getFrom().to_xml(os, indent);
		os << indent << "</from>";
		os << indent << "<report>";
		getReport().to_xml(os, indent);
		os << indent << "</report>";
		os << indent << "<text>" << getText() << "</text>";
	}
	
	// restore from xml stream
	void EnforcementReportPackageCore::from_xml(std::istream &is) {
		size_t counter = 0;
		
		static const Smart::KnuthMorrisPratt kmp_id("<id>");
		static const Smart::KnuthMorrisPratt kmp_toList("<toList n=\"");
		static const Smart::KnuthMorrisPratt kmp_to("\">");
		static const Smart::KnuthMorrisPratt kmp_from("<from>");
		static const Smart::KnuthMorrisPratt kmp_report("<report>");
		static const Smart::KnuthMorrisPratt kmp_text("<text>");
		
		if(kmp_id.search(is)) {
			SmartInstitutionsServiceRepository::PackageIdentifier idItem;
			idItem.from_xml(is);
			setId(idItem);
		}
		if(kmp_toList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			SmartInstitutionsServiceRepository::MemberIdentifier toItem;
			std::vector<SmartInstitutionsServiceRepository::MemberIdentifier> toList;
			kmp_to.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_to.search(is)) {
					toItem.from_xml(is);
					toList.push_back(toItem);
				}
			}
			setTo(toList);
		}
		if(kmp_from.search(is)) {
			SmartInstitutionsServiceRepository::MemberIdentifier fromItem;
			fromItem.from_xml(is);
			setFrom(fromItem);
		}
		if(kmp_report.search(is)) {
			SmartInstitutionsServiceRepository::InstitutionStatus reportItem;
			reportItem.from_xml(is);
			setReport(reportItem);
		}
		if(kmp_text.search(is)) {
			std::string textItem;
			is >> textItem;
			setText(textItem);
		}
	}
	
