	const char* PackageIdentifierCore::getCompiledHash()
	{
		return SmartInstitutions_ServiceRepositoryIDL::REPO_HASH;
	}
	
	void PackageIdentifierCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
		// get hash value(s) for CommBasicObjects::CommTimeStamp(idl_PackageIdentifier.timestampe)
		CommBasicObjects::CommTimeStamp::getAllHashValues(hashes);
	}
	
	void PackageIdentifierCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "PackageIdentifierCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
		// check hash value(s) for CommBasicObjects::CommTimeStamp(idl_PackageIdentifier.timestampe)
		CommBasicObjects::CommTimeStamp::checkAllHashValues(hashes);
	}
	
	#ifdef ENABLE_HASH
	size_t PackageIdentifierCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.id);
		seed += CommBasicObjects::CommTimeStamp::generateDataHash(data.timestampe);
		
		return seed;
	}
	#endif
	
	// default constructor
	PackageIdentifierCore::PackageIdentifierCore()
	:	idl_PackageIdentifier()
	{  
		setId(0);
		setTimestampe(CommBasicObjects::CommTimeStamp());
	}
	
	PackageIdentifierCore::PackageIdentifierCore(const DATATYPE &data)
	:	idl_PackageIdentifier(data)
	{  }

	PackageIdentifierCore::~PackageIdentifierCore()
	{  }
	
	void PackageIdentifierCore::to_ostream(std::ostream &os) const
	{
	  os << "PackageIdentifier(";
	  os << getId() << " ";
	  getTimestampe().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void PackageIdentifierCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<id>" << getId() << "</id>";
		os << indent << "<timestampe>";
		getTimestampe().to_xml(os, indent);
		os << indent << "</timestampe>";
	}
	
	// restore from xml stream
	void PackageIdentifierCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_id("<id>");
		static const Smart::KnuthMorrisPratt kmp_timestampe("<timestampe>");
		
		if(kmp_id.search(is)) {
			unsigned long int idItem;
			is >> idItem;
			setId(idItem);
		}
		if(kmp_timestampe.search(is)) {
			CommBasicObjects::CommTimeStamp timestampeItem;
			timestampeItem.from_xml(is);
			setTimestampe(timestampeItem);
		}
	}
	
