Smart::StatusCode ComponentB::connectQuery_ValidCoordinate2D_Request_Port(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	if(connections.query_ValidCoordinate2D_Request_Port.initialConnect == false) {
		return Smart::SMART_OK;
	}
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = query_ValidCoordinate2D_Request_Port->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->query_ValidCoordinate2D_Request_Port->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	return status;
}


