/**
 * Start all tasks contained in this component.
 */
void ComponentB::startAllTasks() {
	// start task MainB
	if(connections.mainB.scheduler != "DEFAULT") {
		ACE_Sched_Params mainB_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.mainB.scheduler == "FIFO") {
			mainB_SchedParams.policy(ACE_SCHED_FIFO);
			mainB_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.mainB.scheduler == "RR") {
			mainB_SchedParams.policy(ACE_SCHED_RR);
			mainB_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		mainB->start(mainB_SchedParams, connections.mainB.cpuAffinity);
	} else {
		mainB->start();
	}
}

