	void CommTaskEventStateCore::to_ostream(std::ostream &os) const
	{
	  os << "CommTaskEventState(";
	  os << getRobotTarget() << " ";
	  getJob().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void CommTaskEventStateCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<robotTarget>" << getRobotTarget() << "</robotTarget>";
		os << indent << "<job>";
		getJob().to_xml(os, indent);
		os << indent << "</job>";
	}
	
	// restore from xml stream
	void CommTaskEventStateCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_robotTarget("<robotTarget>");
		static const Smart::KnuthMorrisPratt kmp_job("<job>");
		
		if(kmp_robotTarget.search(is)) {
			std::string robotTargetItem;
			is >> robotTargetItem;
			setRobotTarget(robotTargetItem);
		}
		if(kmp_job.search(is)) {
			CommBasicObjects::CommTaskMessage jobItem;
			jobItem.from_xml(is);
			setJob(jobItem);
		}
	}
	
