void CommPlannerEventResult::get(char* r)const
{
  switch(idl_CommPlannerEventResult.state) {
    case PlannerEventType::PLANNER_NO_ERROR:
      strcpy(r,"(ok)");
      break;
    case PlannerEventType::PLANNER_UNKNOWN_ERROR:
      strcpy(r,"(unknown error)");
      break;
    case PlannerEventType::PLANNER_NO_GOAL_AVAILABLE:
      strcpy(r,"(no goal available)");
      break;
    case PlannerEventType::PLANNER_GOAL_OK:
      strcpy(r,"(goal ok)");
      break;
    case PlannerEventType::PLANNER_GOAL_NOT_MARKED:
      strcpy(r,"(goal not marked)");
      break;
    case PlannerEventType::PLANNER_START_OCCUPIED_OBSTACLE:
      strcpy(r,"(start occupied by obstacle)");
      break;
    case PlannerEventType::PLANNER_START_OCCUPIED_GOAL:
      strcpy(r,"(start occupied by goal)");
      break;
    case PlannerEventType::PLANNER_NO_PATH:
      strcpy(r,"(no path)");
      break;
    case PlannerEventType::PLANNER_PATH_FOUND:
      strcpy(r,"(path found)");
      break;
    case PlannerEventType::PLANNER_WRONG_MAPID:
      strcpy(r,"(wrong map id)");
      break;
    case PlannerEventType::PLANNER_INVALID_MAP:
      strcpy(r,"(invalid map)");
      break;
    case PlannerEventType::PLANNER_UNKNOWN:
      strcpy(r,"(unknown)");
      break;
    default:
      strcpy(r,"(error)");
      break;
  }
}

