	void Coordinate3DPlusCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "Coordinate3DPlusCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
		// check hash value(s) for CommBasicObjects::CommPosition3dWithCovariance(idl_Coordinate3DPlus.plus)
		CommBasicObjects::CommPosition3dWithCovariance::checkAllHashValues(hashes);
	}
	
	#ifdef ENABLE_HASH
	size_t Coordinate3DPlusCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.x);
		boost::hash_combine(seed, data.y);
		boost::hash_combine(seed, data.z);
		seed += CommBasicObjects::CommPosition3dWithCovariance::generateDataHash(data.plus);
		
		return seed;
	}
	#endif
	
