	void Coordinate3DPlusCore::to_ostream(std::ostream &os) const
	{
	  os << "Coordinate3DPlus(";
	  os << getX() << " ";
	  os << getY() << " ";
	  os << getZ() << " ";
	  getPlus().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void Coordinate3DPlusCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<x>" << getX() << "</x>";
		os << indent << "<y>" << getY() << "</y>";
		os << indent << "<z>" << getZ() << "</z>";
		os << indent << "<plus>";
		getPlus().to_xml(os, indent);
		os << indent << "</plus>";
	}
	
	// restore from xml stream
	void Coordinate3DPlusCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_x("<x>");
		static const Smart::KnuthMorrisPratt kmp_y("<y>");
		static const Smart::KnuthMorrisPratt kmp_z("<z>");
		static const Smart::KnuthMorrisPratt kmp_plus("<plus>");
		
		if(kmp_x.search(is)) {
			double xItem;
			is >> xItem;
			setX(xItem);
		}
		if(kmp_y.search(is)) {
			double yItem;
			is >> yItem;
			setY(yItem);
		}
		if(kmp_z.search(is)) {
			double zItem;
			is >> zItem;
			setZ(zItem);
		}
		if(kmp_plus.search(is)) {
			CommBasicObjects::CommPosition3dWithCovariance plusItem;
			plusItem.from_xml(is);
			setPlus(plusItem);
		}
	}
	
