	const char* NestedImportCore::getCompiledHash()
	{
		return DomainTSDemoIDL::REPO_HASH;
	}
	
	void NestedImportCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
		// get hash value(s) for DomainTSDemo::Coordinate3D(idl_NestedImport.a)
		DomainTSDemo::Coordinate3D::getAllHashValues(hashes);
		// get hash value(s) for DomainTSDemo::Coordinate3DPlus(idl_NestedImport.aa)
		DomainTSDemo::Coordinate3DPlus::getAllHashValues(hashes);
		// get hash value(s) for CommNavigationObjects::CommClosestNodeRequest(idl_NestedImport.aaa)
		CommNavigationObjects::CommClosestNodeRequest::getAllHashValues(hashes);
	}
	
	void NestedImportCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "NestedImportCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
		// check hash value(s) for DomainTSDemo::Coordinate3D(idl_NestedImport.a)
		DomainTSDemo::Coordinate3D::checkAllHashValues(hashes);
		// check hash value(s) for DomainTSDemo::Coordinate3DPlus(idl_NestedImport.aa)
		DomainTSDemo::Coordinate3DPlus::checkAllHashValues(hashes);
		// check hash value(s) for CommNavigationObjects::CommClosestNodeRequest(idl_NestedImport.aaa)
		CommNavigationObjects::CommClosestNodeRequest::checkAllHashValues(hashes);
	}
	
	#ifdef ENABLE_HASH
	size_t NestedImportCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		seed += DomainTSDemo::Coordinate3D::generateDataHash(data.a);
		seed += DomainTSDemo::Coordinate3DPlus::generateDataHash(data.aa);
		seed += CommNavigationObjects::CommClosestNodeRequest::generateDataHash(data.aaa);
		
		return seed;
	}
	#endif
	
