	void NestedImportCore::to_ostream(std::ostream &os) const
	{
	  os << "NestedImport(";
	  getA().to_ostream(os);
	  getAa().to_ostream(os);
	  getAaa().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void NestedImportCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<a>";
		getA().to_xml(os, indent);
		os << indent << "</a>";
		os << indent << "<aa>";
		getAa().to_xml(os, indent);
		os << indent << "</aa>";
		os << indent << "<aaa>";
		getAaa().to_xml(os, indent);
		os << indent << "</aaa>";
	}
	
	// restore from xml stream
	void NestedImportCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_a("<a>");
		static const Smart::KnuthMorrisPratt kmp_aa("<aa>");
		static const Smart::KnuthMorrisPratt kmp_aaa("<aaa>");
		
		if(kmp_a.search(is)) {
			DomainTSDemo::Coordinate3D aItem;
			aItem.from_xml(is);
			setA(aItem);
		}
		if(kmp_aa.search(is)) {
			DomainTSDemo::Coordinate3DPlus aaItem;
			aaItem.from_xml(is);
			setAa(aaItem);
		}
		if(kmp_aaa.search(is)) {
			CommNavigationObjects::CommClosestNodeRequest aaaItem;
			aaaItem.from_xml(is);
			setAaa(aaaItem);
		}
	}
	
