	const char* TimeObjectCore::getCompiledHash()
	{
		return DomainTSDemoIDL::REPO_HASH;
	}
	
	void TimeObjectCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
		// get hash value(s) for DomainTSDemo::BasicTime(idl_TimeObject.basic)
		DomainTSDemo::BasicTime::getAllHashValues(hashes);
		// get hash value(s) for CommBasicObjects::CommTimeStamp(idl_TimeObject.compound)
		CommBasicObjects::CommTimeStamp::getAllHashValues(hashes);
	}
	
	void TimeObjectCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "TimeObjectCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
		// check hash value(s) for DomainTSDemo::BasicTime(idl_TimeObject.basic)
		DomainTSDemo::BasicTime::checkAllHashValues(hashes);
		// check hash value(s) for CommBasicObjects::CommTimeStamp(idl_TimeObject.compound)
		CommBasicObjects::CommTimeStamp::checkAllHashValues(hashes);
	}
	
	#ifdef ENABLE_HASH
	size_t TimeObjectCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		seed += DomainTSDemo::BasicTime::generateDataHash(data.basic);
		seed += CommBasicObjects::CommTimeStamp::generateDataHash(data.compound);
		
		return seed;
	}
	#endif
	
	// default constructor
	TimeObjectCore::TimeObjectCore()
	:	idl_TimeObject()
	{  
		setBasic(DomainTSDemo::BasicTime());
		setCompound(CommBasicObjects::CommTimeStamp());
	}
	
	TimeObjectCore::TimeObjectCore(const DATATYPE &data)
	:	idl_TimeObject(data)
	{  }

	TimeObjectCore::~TimeObjectCore()
	{  }
	
	void TimeObjectCore::to_ostream(std::ostream &os) const
	{
	  os << "TimeObject(";
	  getBasic().to_ostream(os);
	  getCompound().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void TimeObjectCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<basic>";
		getBasic().to_xml(os, indent);
		os << indent << "</basic>";
		os << indent << "<compound>";
		getCompound().to_xml(os, indent);
		os << indent << "</compound>";
	}
	
	// restore from xml stream
	void TimeObjectCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_basic("<basic>");
		static const Smart::KnuthMorrisPratt kmp_compound("<compound>");
		
		if(kmp_basic.search(is)) {
			DomainTSDemo::BasicTime basicItem;
			basicItem.from_xml(is);
			setBasic(basicItem);
		}
		if(kmp_compound.search(is)) {
			CommBasicObjects::CommTimeStamp compoundItem;
			compoundItem.from_xml(is);
			setCompound(compoundItem);
		}
	}
	
